mapper.select:
select * from comm_task where 1=1

    and task_no = #{taskNo}


    and task_type = #{taskType}


    and task_subject = #{taskSubject}


    and task_content = #{taskContent}


    and system_name = #{systemName}


    and estimated_effort = #{estimatedEffort}


    and actual_effort = #{actualEffort}


    and assignee = #{assignee}


    and severity = #{severity}


    and status = #{status}


    and attached_id = #{attachedId}


    and remarks = #{remarks}


    and submission_date = #{submissionDate}

mapper.update:
update comm_task
set timestamp = #{timestamp}

    , task_type = #{taskType}


    , task_subject = #{taskSubject}


    , task_content = #{taskContent}


    , system_name = #{systemName}


    , estimated_effort = #{estimatedEffort}


    , actual_effort = #{actualEffort}


    , assignee = #{assignee}


    , severity = #{severity}


    , status = #{status}


    , attached_id = #{attachedId}


    , remarks = #{remarks}

where task_no = #{taskNo}
